#!/bin/bash

files=$(cat resources/ukb/pon_file_paths.txt)

IFS=$'\n' 
for file in $files 
do 
name=$(echo $file | sed -e 's/\/Bulk\/Exome\\ sequences\/Exome\\ OQFE\\ CRAM\\ files\/[0-9][0-9]\///') 

#echo $name

new_file=" -V /home/dnanexus/$name"
echo $new_file >> pon.dnanexus.files.txt

done

